<?phpclass Commercers_Autocategory_Model_System_Config_Source_Cron_Frequency extends Mage_Adminhtml_Model_System_Config_Source_Cron_Frequency{    const CRON_MANUAL = 'MANUAL';    const CRON_MINUTE = 'M';    const CRON_TENMINUTES = '10M';    const CRON_TWENTYMINUTES = '20M';    const CRON_HALFHOURLY = 'HH';    const CRON_HOURLY = 'H';    const CRON_DAILY = 'D';    const CRON_TWICEDAILY = 'TD';    const CRON_WEEKLY = 'W';    public function toOptionArray()    {        if (!self::$_options) {            self::$_options = array(                array(                    'label' => Mage::helper('cron')->__('Manual'),                    'value' => self::CRON_MANUAL,                ),                array(                    'label' => Mage::helper('cron')->__('Hourly'),                    'value' => self::CRON_HOURLY,                ),                array(                    'label' => Mage::helper('cron')->__('Daily'),                    'value' => self::CRON_DAILY,                ),                array(                    'label' => Mage::helper('cron')->__('Twice Daily'),                    'value' => self::CRON_TWICEDAILY,                ),                array(                    'label' => Mage::helper('cron')->__('Weekly'),                    'value' => self::CRON_WEEKLY,                ),                array(                    'label' => Mage::helper('cron')->__('Monthly'),                    'value' => self::CRON_MONTHLY,                ),            );        }        return self::$_options;    }}